// Libs
import React, { useState } from 'react';
import styled from 'styled-components';

// Images
import CloseIcon from '../assets/x.svg';

//Styles
const Container = styled.div`
  margin-bottom: 2rem;  
  width: 20rem;
  height: 22rem;
  border: 1px solid;
  border-radius: 8px;
  box-shadow: 5px 2px 10px 0px #2121213d;
`;

const Image = styled.div`
  width: 20rem;
  height: 18rem;
  background-color: red;

  p {
    margin: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    height: inherit;
  }
`;

const HeroName = styled.p`
  text-align: center;
  font-size: 1rem;
  font-weight: 600;
  word-break: break-all;
`;

const Overlay = styled.div`
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  background: rgba(196, 196, 196, 0.3);
  z-index: 2;
`;

const ContainerDetails = styled.div`
  position: relative;
  width: 40%;
  height: 60vh;
  background-color: #fff;
`;

const DetailsClose = styled.img`
  position: absolute;
  right: 0;
  width: 2rem;
  cursor: pointer;
`;

function Card(){
  const [handleAppearance, setHandleAppearance] = useState(false);


  const handleOpenDetails = () => {
    setHandleAppearance(!handleAppearance)
  }

  const renderAppearance = () => {
    return (
      <Overlay>
        <ContainerDetails>
          <DetailsClose
            src={CloseIcon}
            alt='close'
            onClick={handleOpenDetails}
          />
          <p>aaaaaaa</p>
          <p>aaaaaaa</p>
          <p>aaaaaaa</p>
          <p>aaaaaaa</p>
        </ContainerDetails>
      </Overlay>
    )
  }

  return (
    <Container onClick={handleOpenDetails}>
      <Image>
        <p>imagem</p>
      </Image>
      <HeroName>name</HeroName>
      {handleAppearance && renderAppearance()}
    </Container>
  )
}

export default Card;
