// Libs
import React, { Component } from 'react';
import styled from 'styled-components';

// Components
import Header from '../components/Header';
import Input from '../components/Input';
import Button from '../components/Button';

// Images
import editIcon from '../assets/edit.svg';
import deleteIcon from '../assets/x-circle.svg';

// Styles
const Container = styled.div`
  height: 100vh;
  font-family: 'Overpass', Regular;
  background: #f2f4f7;
`;

const ContainerTodo = styled.div`
  margin: 2rem;
  padding-bottom: 1rem;
  width: 25%;
  max-width: 35%;
  height: fit-content;
  border: 2px solid #c7cad0;
  border-radius: 10px;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  text-align: center;
`;

const ContentTodo = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ContainerList = styled.ul`
  margin: 2rem;
  padding: 0;
  width: 60%;
  max-width: 60%;
  height: 80vh;
  border: 2px solid #c7cad0;
  border-radius: 10px;
`;

const ListItem = styled.li`
  padding: 1rem;
  display: flex;
  justify-content: space-between;
  list-style: none;
  border-bottom: 1px solid #c7cad0;

  &:hover {
		background: #c7cad088;
	}
`;

const ListItemText = styled.p`
  width: 90%;
  font-size: 1rem;
  word-break: break-all;
  font-weight: ${(props) => props.title && '800'};
`;

const ListItemOptions = styled.div`
  display: flex;
  align-items: center;
`;

const ItemOptionsIcon = styled.img`
  margin-right: ${(props) => props.first && '.5rem'};
  cursor: pointer;
`;

const EmptyListMessage = styled.p`
  font-size: 1.5rem;
  text-align: center;
`;

const ErrorMessage = styled.p`
  margin: 0;
  font-size: .8rem;
  color: tomato;
  font-weight: bold;
`;

class Dashboard extends Component {
  state = {
    values: {
      title: '',
      task: ''
    },
    titleError: undefined,
    taskError: undefined,
    emptyError: undefined,
    list: [],
  }

  handleSubmit = () => {
    const { error, emptyError } = this.state;
    const { title, task, titleError, taskError } = this.state.values;

    if (title === '' && task === '') {
      this.setState({
        emptyError: 'Preencha todos os campos.',
      });
    } else {
      if (title.length === 0) {
        this.setState({
          emptyError: undefined,
          titleError: 'Insira um tÃ­tulo.',
        });
      } else {
        this.setState({
          titleError: undefined,
        })
      }

      if (task.length === 0) {
        this.setState({
          emptyError: undefined,
          taskError: 'Insira uma tarefa.',
        });
      } else {
        this.setState({
          taskError: undefined,
        })
      }

      if (!error && !emptyError && !titleError && !taskError) {
          console.log('values', this.state.values)

        const item = {title, task}

        this.setState({
          list: this.state.list.concat(item),
        });
      }
    }
  }

  handleChange = (ev, field) => {
    const { values } = this.state;

    values[field] = ev.target.value;

    console.log('values', this.state.values)

    this.setState({
      values,
    });
  }

  renderList = () => {
    return (
      <ContainerList>
        {this.state.list > 0 ? (
          this.state.list.map(item => (
            <ListItem>
              <ListItemText title>{item.title}</ListItemText>
              <ListItemText>{item.task}</ListItemText>
              <ListItemOptions>
                <ItemOptionsIcon first src={editIcon} alt="editar" />
                <ItemOptionsIcon src={deleteIcon} alt="excluir" />
              </ListItemOptions>
            </ListItem>
          ))
        ) : <EmptyListMessage>Adicione uma tarefa.</EmptyListMessage>
        }
      </ContainerList>
    );
  }

  render() {
    const { titleValue, taskValue, titleError, taskError, emptyError } = this.state;

    console.log('listaa', this.state.list)

    return (
      <Container>
        <Header />
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <ContainerTodo>
            <Title>A Fazer</Title>
            <ContentTodo>
              <Input
                text={titleValue}
                type="text"
                label="TÃ­tulo"
                containerWidth="75%"
                inputWidth="100%"
                borderBottom={emptyError ? "2px solid tomato" : "2px solid #d7d9dc"}
                onChange={(ev) => this.handleChange(ev, 'title')}
              />
              {titleError && <ErrorMessage>{titleError}</ErrorMessage>}
              <Input
                text={taskValue}
                type="text"
                label="Tarefa"
                containerWidth="75%"
                inputWidth="100%" 
                borderBottom={emptyError ? "2px solid tomato" : "2px solid #d7d9dc"}
                onChange={(ev) => this.handleChange(ev, 'task')}
              />
              {taskError && <ErrorMessage>{taskError}</ErrorMessage>}
              {emptyError && <ErrorMessage>{emptyError}</ErrorMessage>}
              <Button 
                text="Adicionar"
                onClick={this.handleSubmit}
              />
            </ContentTodo>
          </ContainerTodo>
            {this.renderList()}
        </div>
      </Container>
    )
  }
}

export default Dashboard;
