      // } else {
      //   console.log('falseeee')
      // }

      let teste = (document.body.scrollTop > 20 || document.documentElement.scrollTop > 20) && this.setState({ isButtonShowing: true });
    }   
  }

  fetchingData = async () => {
    try {
      const response = await getAllPics();

      this.setState({
        picsList: response.data,
      });

      console.log('response', response)
    } catch(error) {
      console.log('error', error);
    }
  }

  teste = (lastOne) => {
    // const lastOne = this.state.picsList.length - 1;

    // if (document.body.scrollTop > 20 || document.documentElement.scrollTop > 20) {

    // } else {
    //   mybutton.style.display = "none";
    // }

    console.log('lastOne', lastOne)
  }

  render() {
    console.log('testandoHein', this.state.testandoHein)
    return (
      <Container>
        <Header />
        <Content>
          {this.state.picsList.map((item, index) => (
            <ContainerImage key={index}>
              <img src={item.urls.small} alt={item.alt_description} />
            </ContainerImage> 
          ))}
          <Button 
            display={this.state.isButtonShowing} 
            onClick={this.teste}
            >
              clica aqui
          </Button>
        </Content>
      </Container>
    );
  }
}

export default Search;
