// Action Type
const ADD_HERO = 'superHeros/ADD/ADD_TASK';
const EDIT_TASK = 'superHeros/EDIT/EDIT_TASK';
const DELETE_TASK = 'superHeros/DELETE/DELETE_TASK';

const initialState = {
  list: [],
};

// Reducers
export default function Reducer(state = initialState, action) {
  switch(action.type) {
    case ADD_TASK:
      return Object.assign({}, state, {
        list: [
          ...state.list, action.info
        ]
      });
    case EDIT_TASK:
      return {
        ...state,
        list: action.info,
      };
    case DELETE_TASK: 
      return Object.assign({}, state, {
        list: state.list.filter(item => item.id !== action.info.id)
      });
    default: return state;
  }
}

// Action Creator
export const addHero = info => ({
  type: ADD_TASK,
  info,
});

export const editTask = info => ({
  type: EDIT_TASK,
  info,
});

export const deleteTask = info => ({
  type: DELETE_TASK,
  info,
})
