// Libs
import React, { Component } from 'react';
import styled from 'styled-components';
import { connect } from 'react-redux';
import { useDispatch, useSelector } from "react-redux";

// Components
import Header from '../components/Header';
import Card from '../components/Card';
import Input from '../components/Input';

// // Images
import searchIcon from '../assets/search.svg';

// Services
import { getHeroById, getHeroByName } from '../services/apiSuperHero';

// Redux
import { addHero, deleteTask, editTask } from '../dataflow/modules/heroes-modules';

const mapStateToProps = (state) => ({
  list: state.heroes.list,
})

const mapDispatchToProps = (dispatch) => ({
  addHero: (info) => dispatch(addHero(info)),
  // deleteTask: (info) => dispatch(deleteTask(info)),
  // editTask: (info) => dispatch(editTask(info)),
});

// Styles
const Container = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;

  @media(max-width: 648px) {
    flex-direction: column;
  }
`;

const Content = styled.div`
  width: 80%;
  height: 100vh;
  display: flex;
  background-color: #f2f4f7;

  @media(max-width: 648px) {
    width: 100%;
  } 
`;

const Wrapper = styled.div`
  padding-top: 2rem;
  width: 100%;
  display: flex;
  justify-content: space-between;

  @media(max-width: 768px) {
    display: flex;
    flex-direction: column;
  }
`;

const ContainerTitle = styled.div`
  margin: 2rem;
  width: 30%;
  max-width: 30%;
  height: fit-content;

  @media(max-width: 768px) {
    margin: 0;
    padding: 0 3rem;
    width: 100%;
    max-width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  }

  @media(max-width: 648px) {
    margin-left: 0;
    margin-bottom: 2rem;
  }
`;

const Title = styled.h2`
  font-size: 1.5rem;
  text-align: center;
`;

const ContainerSearch = styled.div`
  margin-top: 2rem;
  padding: 1rem;
  width: 100%;
  border: 1px solid #2EC4B6;
  border-radius: 10px;

  @media(max-width: 768px) {
    padding: 0;
    width: 60%;
    display: flex;
    justify-content: center;
    border-radius: 20px;
  }

  @media(max-width: 648px) {
    width: 85%;
  }
`;

const ContainerList = styled.div`
  margin-top: 2rem;
  width: 100%;
  height: 95vh;
  display: inline-grid;
  justify-items: center;
  align-items: flex-start;
  grid-template-columns: repeat(2, 1fr);
  grid-row-gap: 1px;
  overflow-y: scroll;

  ::-webkit-scrollbar {
			width: 4px;
		}
		::-webkit-scrollbar-track {
			background-color: transparent;
		}
		::-webkit-scrollbar-thumb {
			background-color: transparent;
		}
		::-webkit-scrollbar-thumb:hover {
			background-color: transparent;
		}

  @media(max-width: 1024px) {
    padding: 0 5rem;
    grid-template-columns: repeat(1, 1fr);
  }

  @media(max-width: 768px) {
    padding: 0 3rem;
  }

  @media(max-width: 375px) {
    padding: 0 1.55rem;
  }
`;

const EmptyHeroList = styled.p`
  margin-top: 15rem;
  margin-left: 10rem;
  font-weight: 600;
  font-size: 1.4rem;
`;

class SuperHeros extends Component {
  state = {
    superHerosList: [],
    searchValue: '',
    searchValueError: undefined,
    isFetching: false,
  }

  componentDidMount() {
    this.fetchingGetAllHeroes();
  }

  fetchingGetAllHeroes = async () => {
    try {
      // let i = 1;
      // while(i <= 3) {
      //   const response = await getHeroById(Math.floor(Math.random() * 731 + 1));

      //   if (response) {
      //     setTimeout(() => {
      //       setSuperHeroList(superHerosList.push(response));
      //     }, 3000);
      //     setSuperHeroList(superHerosList.push(response));
      //   }

      //   i += 1;
      // }
    } catch(error) {
      console.log('error', error);
      console.log('error - response', error.response);
    }
  }

  fetchingGetHeroByName = async (heroName) => {
    try {
      this.setState({
        isFetching: true,
      });

      const response = await getHeroByName(heroName);

      if(response) {
        this.setState({
          superHerosList: response.data.results,
        });

        this.props.addHero(response.data.results);
      }

      console.log('response', response);

    } catch(error) {
      console.log('error', error);
      console.log('error - response', error.response);
    }
  }

  handleValidateSearchValue = () => {
    const { searchValue } = this.state;

    if (!searchValue) {
      this.setState({
        searchValueError: 'Insira o nome do personagem',
      });
    } else {

      // Colocar a primeira letra das palavras em maiúsculo. No primeiro caso, verifica se há mais de duas palavras.
      if (searchValue.includes(' ')) {
        var value = searchValue.split(' ');
        var newValue = [];

        for(var x = 0; x < value.length; x++){
          newValue.push(value[x].charAt(0).toUpperCase()+value[x].slice(1));
        }

        this.fetchingGetHeroByName(newValue.join(' '));
      } else {
        const formating = searchValue[0].toUpperCase() + searchValue.slice(1).toLowerCase();

        this.fetchingGetHeroByName(formating);
      }
    }
  }

  handleSearchValue = (event) => {
    this.setState({
      searchValue: event.target.value,
    });
  }

  renderHerosList = () => {
    const { list } = this.props;

    return (
      <ContainerList>
        {list.length > 0 ? list[0].map(item => (
          <Card hero={item} />
        )) : <EmptyHeroList>Busque por um personagem! :)</EmptyHeroList>}
      </ContainerList>
    );
  }

  render() {
    const { searchValue, searchValueError } = this.state;

    return (
      <Container>  
        <Header />
        <Content>
          <Wrapper>
            <ContainerTitle>
              <Title>Super Heróis</Title>
              <ContainerSearch>
                <Input 
                  label="Pesquise por um Super Herói"
                  placeholder="Pesquise por um Super Herói"
                  value={searchValue}
                  onChange={this.handleSearchValue}
                  inputWidth="92%"
                  inputWidthTablet="100%"
                  inputMarginBottomTablet="0"
                  borderBottom="1px solid #2EC4B6"
                  borderBottomMob="transparent"
                  search={true}
                  // error={passwordError}
                />
                <img
                  src={searchIcon}
                  alt="search"
                  onClick={this.handleValidateSearchValue}
                />
                {searchValueError && <p>{searchValueError}</p>}
              </ContainerSearch>
            </ContainerTitle>
            {this.renderHerosList()}
          </Wrapper>
        </Content>
      </Container>
    )
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(SuperHeros);
