// Libs
import React from 'react';
import styled from 'styled-components';
import { NavLink, Redirect } from 'react-router-dom';

// Images
import aperture from '../assets/aperture.svg';

// Styles
const Container = styled.div`
  padding: 1rem 0;
  width: 100%;
  height: 4rem;
  display: flex;
  justify-content: center;
`;

const Content = styled.div`
  width: 95%;
  height: 4rem;
  display: flex;
  align-items: center;
  border: 1px solid #e7e9eb;
  border-radius: 20px;
  background: #fff;
`;

const Header = () => {
  renderMenu = () => (
    <div>
      <NavLink
        exact to="/documents"
        activeClassName="button-header-dash"
      >
        Dashboard
      </NavLink>
    </div>
  );

  return (
    <Container>
      <Content>
        <img src={aperture} alt="aperture" />
        {renderMenu()}
        <p>Yasmin</p>
      </Content>
    </Container>
  )
};

export default Header;
