// Action Type
const ADD_TASK = 'simple/ADD/ADD_TASK';


const initialState = {
  list: [],
};

export default function Reducer(state = initialState, action) {
  switch(action.type) {
    case ADD_TASK:
      return Object.assign({}, state, {
        list: [
          ...state.list, action.info
        ]
      });
    default: return state;
  }
}

// Action Creator
export const addTask = info => ({
  type: ADD_TASK,
  info,
})
