// Action Types
const NEW_USER = 'simple/register/NEW_USER';

const initialState = {
  user: {
    username: '',
    email: '',
    password: '',
  }
}

export default function Reducer(state = initialState, action) {
  switch(action.type) {
    case NEW_USER:
      return Object.assign({}, state, {
        user: {...state.user},
      });
    default: return null
  }
}

// ACTION TYPE
export const newUser = (user) => ({
  type: NEW_USER,
  user,
})
