// Libs
import React, { Component } from 'react';
import styled from 'styled-components';
import { nanoid } from "nanoid";

// Components
import Header from '../components/Header';
import Input from '../components/Input';
import Button from '../components/Button';

// Images
import editIcon from '../assets/edit.svg';
import deleteIcon from '../assets/x-circle.svg';

// Styles
const Container = styled.div`
  height: 100vh;
  font-family: 'Overpass', Regular;
  background: #f2f4f7;
`;

const ContainerTodo = styled.div`
  margin: 2rem;
  padding-bottom: 1rem;
  width: 25%;
  max-width: 35%;
  height: fit-content;
  border: 2px solid #c7cad0;
  border-radius: 10px;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  text-align: center;
`;

const ContentTodo = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ContainerList = styled.ul`
  margin: 2rem;
  padding: 0;
  width: 60%;
  max-width: 60%;
  height: 80vh;
  border: 2px solid #c7cad0;
  border-radius: 10px;
  display: ${(props) => props.emptyMessage && 'flex'};
  justify-content: ${(props) => props.emptyMessage && 'center'};
  align-items: ${(props) => props.emptyMessage && 'center'};
`;

const ListItem = styled.li`
  padding: 1rem;
  display: flex;
  justify-content: space-between;
  list-style: none;
  border-bottom: 1px solid #c7cad0;

  &:hover {
		background: #c7cad088;
	}
`;

const ListItemText = styled.p`
  width: 90%;
  font-size: 1rem;
  word-break: break-all;
  font-weight: ${(props) => props.name && '800'};
`;

const ListItemOptions = styled.div`
  display: flex;
  align-items: center;
`;

const ItemOptionsIcon = styled.img`
  margin-right: ${(props) => props.first && '.5rem'};
  cursor: pointer;
`;

const EmptyListMessage = styled.p`
  font-size: 1.5rem;
  text-align: center;
`;

const ErrorMessage = styled.p`
  margin: 0;
  font-size: .8rem;
  color: tomato;
  font-weight: bold;
`;

class Dashboard extends Component {
  state = {
    values: {
      name: '',
      brand: '',
      dataSheet: '',
    },
    nameError: undefined,
    brandError: undefined,
    dataSheetError: undefined,
    emptyError: undefined,
    list: [],
  }

  handleSubmit = () => {
    const { emptyError, values } = this.state;
    const { name, brand, dataSheet, nameError, brandError, dataSheetError } = values;

    if (name === '' && brand === '' && dataSheet === '') {
      this.setState({
        emptyError: 'Preencha todos os campos.',
      });
    } else {
      this.setState({
        emptyError: undefined,
      });

      if (name.length === 0) {
        this.setState({
          nameError: 'Insira um nome.',
        });
      } else {
        this.setState({
          nameError: undefined,
        })
      }

      if (brand.length === 0) {
        this.setState({
          brandError: 'Insira uma marca.',
        });
      } else {
        this.setState({
          brandError: undefined,
        })
      }

      if (dataSheet.length === 0) {
        this.setState({
          dataSheetError: 'Insira uma ficha técnica.',
        });
      } else {
        this.setState({
          dataSheetError: undefined,
        });
      }

      if (!emptyError && !nameError && !brandError && !dataSheetError) {
        const item = {id: nanoid(5), name, brand, dataSheet}

        this.setState({
          list: this.state.list.concat(item),
          // values: {
          //   name: '',
          //   brand: '',
          //   dataSheet: '',
          // }
        });


        this.setState({
          values: {
            name: '',
            brand: '',
            dataSheet: '',
          }
        })

        console.log('values', this.state.values)


      }
    }
  }

  handleChange = (ev, field) => {
    const { values } = this.state;

    values[field] = ev.target.value;

    console.log("values change", values)

    this.setState({
      values,
    });
  }

  handleDeleteModel = (model) => {
    const { list } = this.state;

    const del = list.filter(item => item.id !== model.id);

    this.setState({
      list: del,
    });
  }

  handleEditModel = (model) => {
    console.log('aaaa000')

    console.log('model', model)
  }

  renderList = () => {
    const { list } = this.state;

    return (
      <ContainerList emptyMessage={list.length === 0}>
        {list.length > 0 ? (
          list.map(item => (
            <ListItem>
              <ListItemText name>{item.name}</ListItemText>
              <ListItemText>{item.brand}</ListItemText>
              <ListItemText>{item.dataSheet}</ListItemText>
              <ListItemOptions>
                <ItemOptionsIcon first src={editIcon} alt="editar" onClick={() => this.handleEditModel(item)} />
                <ItemOptionsIcon src={deleteIcon} alt="excluir" onClick={() => this.handleDeleteModel(item)} />
              </ListItemOptions>
            </ListItem>
          ))
        ) : <EmptyListMessage>Adicione uma modelo de smartphone.</EmptyListMessage>
        }
      </ContainerList>
    );
  }

  render() {
    const { nameError, brandError, emptyError, dataSheetError, values } = this.state;
    const { name, brand, dataSheet } = values;

    console.log('listaa', this.state.list)

    return (
      <Container>
        <Header />
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <ContainerTodo>
            <Title>Adicione um Modelo</Title>
            <ContentTodo>
              <Input
                text={name}
                type="text"
                label="Nome"
                containerWidth="75%"
                inputWidth="100%"
                borderBottom={emptyError ? "2px solid tomato" : "2px solid #d7d9dc"}
                onChange={(ev) => this.handleChange(ev, 'name')}
              />
              {nameError && <ErrorMessage>{nameError}</ErrorMessage>}
              <Input
                text={brand}
                type="text"
                label="Marca"
                containerWidth="75%"
                inputWidth="100%" 
                borderBottom={emptyError ? "2px solid tomato" : "2px solid #d7d9dc"}
                onChange={(ev) => this.handleChange(ev, 'brand')}
              />
              {brandError && <ErrorMessage>{brandError}</ErrorMessage>}
              <Input
                text={dataSheet}
                type="text"
                label="Ficha Técnica"
                containerWidth="75%"
                inputWidth="100%" 
                borderBottom={emptyError ? "2px solid tomato" : "2px solid #d7d9dc"}
                onChange={(ev) => this.handleChange(ev, 'dataSheet')}
              />
              {dataSheetError && <ErrorMessage>{dataSheetError}</ErrorMessage>}
              {emptyError && <ErrorMessage>{emptyError}</ErrorMessage>}
              <Button 
                text="Adicionar"
                onClick={this.handleSubmit}
              />
            </ContentTodo>
          </ContainerTodo>
            {this.renderList()}
        </div>
      </Container>
    )
  }
}

export default Dashboard;
