// Action Type
const ADD_HERO = 'superHeroes/ADD/ADD_HERO';
const SEARCH_HERO = 'superHeroes/SEARCH/SEARCH_HERO';
// const EDIT_TASK = 'superHeros/EDIT/EDIT_TASK';
// const DELETE_TASK = 'superHeros/DELETE/DELETE_TASK';

const initialState = {
  list: [],
};

// Reducers
export default function Reducer(state = initialState, action) {
  switch(action.type) {
    case ADD_HERO:
      return Object.assign({}, state, {
        list: [
          ...state.list, action.info
        ]
      });
    case SEARCH_HERO:
    return Object.assign({}, state, {
      list: [
        action.info
      ]
    });
