// Libs
import React, { Component } from 'react';
import styled from 'styled-components';

// Components
import Header from '../components/Header';

// Services
import { getAllPics } from '../services/api';

// Styles
const Container = styled.div`
  width: 100%;
  background: #f2f4f7;
  font-family: 'Overpass', Regular;
`;

const Content = styled.div`
  width: 100%;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
`;

const ContainerImage = styled.div`
  padding: 1rem;
  display: flex;
  justify-content: center;
  align-self: center;
`;

const Button = styled.button`
  position: fixed;
  bottom: 0;
  right: 1;
  padding: 1rem;
`;

class Search extends Component {
  state = {
    picsList: [],
  }

  componentDidMount() {

    this.fetchingData();
  }

  fetchingData = async () => {
    try {
      const response = await getAllPics();

      this.setState({
        picsList: response.data,
      });

      console.log('response', response)
    } catch(error) {
      console.log('error', error);
    }
  }

  render() {
    return (
      <Container>
        <Header />
        <Content>
          {this.state.picsList.map((item, index) => (
            <ContainerImage key={index}>
              <img src={item.urls.small} alt={item.alt_description} />
            </ContainerImage> 
          ))}
          {this.state.picsList.length - 1 && (
            <Button>clica aqui</Button>
          )}
        </Content>
      </Container>
    );
  }
}

export default Search;
