// Libs
import React, { Component } from 'react';
import styled from 'styled-components';
import { nanoid } from "nanoid";

// Components
import Header from '../components/Header';
import Input from '../components/Input';
import Button from '../components/Button';

// Images
import editIcon from '../assets/edit.svg';
import deleteIcon from '../assets/x-circle.svg';

// Styles
const Container = styled.div`
  height: 100vh;
  font-family: 'Overpass', Regular;
  background: #f2f4f7;
`;

const ContainerTodo = styled.div`
  margin: 2rem;
  padding-bottom: 1rem;
  width: 25%;
  max-width: 35%;
  height: fit-content;
  border: 2px solid #c7cad0;
  border-radius: 10px;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  text-align: center;
`;

const ContentTodo = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ContainerList = styled.ul`
  margin: 2rem;
  padding: 0;
  width: 60%;
  max-width: 60%;
  height: 80vh;
  border: 2px solid #c7cad0;
  border-radius: 10px;
  display: ${(props) => props.emptyMessage && 'flex'};
  justify-content: ${(props) => props.emptyMessage && 'center'};
  align-items: ${(props) => props.emptyMessage && 'center'};
`;

const ListItem = styled.li`
  padding: 1rem;
  display: flex;
  justify-content: space-between;
  list-style: none;
  border-bottom: 1px solid #c7cad0;

  &:hover {
		background: #c7cad088;
	}
`;

const ListItemText = styled.p`
  width: 90%;
  font-size: 1rem;
  word-break: break-all;
  font-weight: ${(props) => props.name && '800'};
`;

const ListItemOptions = styled.div`
  display: flex;
  align-items: center;
`;

const ItemOptionsIcon = styled.img`
  margin-right: ${(props) => props.first && '.5rem'};
  cursor: pointer;
`;

const EmptyListMessage = styled.p`
  font-size: 1.5rem;
  text-align: center;
`;

const ErrorMessage = styled.p`
  margin: 0;
  font-size: .8rem;
  color: tomato;
  font-weight: bold;
`;

class Dashboard extends Component {
  state = {
  }

  render() {

    return (
      <Container>
        <Header />
      </Container>
    )
  }
}

export default Dashboard;
