// Libs
import React, { useState } from 'react';
import styled from 'styled-components';
import { NavLink, Redirect } from 'react-router-dom';

// Components
import UserProfile from './UserProfile';

// Images
import apertureIcon from '../assets/aperture.svg';
import HamburgerMenu from '../assets/menu.svg';

// Styles
const Container = styled.header`
  width: 20%;
  height: 100vh;
  display: flex;
  justify-content: center;

  @media(max-width: 768px) {
    width: 25%;
    max-width: 25%;
  }

  @media(max-width: 648px) {
    width: 100%;
    min-width: 100%;
    height: 5rem;
  }
`;

const Content = styled.div`
  position: fixed;
  width: inherit;
  height: -webkit-fill-available;
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  border-radius: 10px;
  background: #fff;

  @media(max-width: 648px) {
    height: 5rem;
    display: flex;
    flex-direction: row;
    align-items: center;
  }
`;

const Logo = styled.img`
  margin: 4rem;
  height: 6rem;

  @media(max-width: 768px) {
    margin: 2rem;
    height: 5rem;
  }

  @media(max-width: 648px) {
    margin: 1rem;
    height: 3rem;
  }
`;

const MobileMenu = styled.img`
  display: none;

  @media(max-width: 648px) {
    margin-right: .5rem;
    height: 2rem;
    display: flex;
  }
`;

const WrapperLinks = styled.div`
  padding: 0 .5rem;
  min-height: 60vh;
  display: flex;
  align-items: baseline;

  @media(max-width: 768px) {
    padding: 0;
    min-height: 50vh;
  }

  @media(max-width: 648px) {
    display: ${(props) => props.display ? 'flex' : 'none'};
  }
`;

const ContainerLinks = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

const Link = styled(NavLink)`
  padding: 1rem;
  margin: 1rem;
  color: #000;
  text-decoration: none;
  text-align: center;

  &:hover {
    color: #fff;
    background: #2EC4B6;
  }

  @media(max-width: 1024px) {
    padding: 1rem;
    margin: 0;
    margin-bottom: 1rem;
  }
`;

const ContainerMenu = styled.div`
  display: flex;
  border-radius: 10px;

  @media(max-width: 1024px) {
    padding: 0 .5rem;
    flex-direction: column;
  }

  @media(max-width: 648px) {
    display: none;
  }
`; 

const ContainerMenuItem = styled.div`
  width: 50%;
  display: flex;
  padding: 1rem 2rem;
  justify-content: center;
  border-bottom: ${(props) => props.user && '0.1px solid #fff'};
  cursor: pointer;

  &:hover {
    background: #2EC4B6;

    p {
      color: #fff;
    }
  }

  @media(max-width: 1024px) {
    margin-bottom: 1rem;
    padding: .5rem;
    width: 100%;
    justify-content: flex-start;
  }
`;

const Text = styled.p`
  margin: .5rem;
  color: #000;
`;

const Header = () => {
  const [redirect, setRedirect] = useState(undefined);
  const [isUserProfileOpen, setUserProfileOpen] = useState(false);
  const [isMobileMenuShowing, setIsMobileMenuShowing] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('sessionToken');

    setRedirect('/');
  }

  const handleMobileMenu = () => {
    setIsMobileMenuShowing(!isMobileMenuShowing);
  }

  const handleUserProfile = () => {
    setUserProfileOpen(true);
  }

  const renderMenu = () => {
    const dataName = localStorage.getItem('username');
    // const username = dataName[0].toUpperCase() + dataName.slice(1).toLowerCase();

    return (
      <ContainerMenu>
        <ContainerMenuItem user onClick={handleUserProfile}>
          {/* <img src={userIcon} alt="user" /> */}
          {/* <Text>{username}</Text> */}
          <Text>Perfil</Text>
        </ContainerMenuItem>
        <ContainerMenuItem onClick={handleLogout}>
          {/* <img src={logoutIcon} alt="logout" /> */}
          <Text>Sair</Text>
        </ContainerMenuItem>
      </ContainerMenu>
    );
  };

  const renderLinks = () => (
    <ContainerLinks>
      <Link
        exact to="/dashboard"
        activeClassName="button-header-dash"
      >
        Dashboard
      </Link>
      <Link
        exact to="/superheros"
        activeClassName="button-header-dash"
      >
        Super Heróis
      </Link>
    </ContainerLinks>
  );

  return (
    <Container>
      <Content>
        <Logo src={apertureIcon} alt="logo" />
        <MobileMenu src={HamburgerMenu} alt="Menu" onClick={handleMobileMenu} />
        <WrapperLinks display={isMobileMenuShowing}>
          {renderLinks()}
        </WrapperLinks>
        {renderMenu()}
      </Content>
      {redirect && <Redirect to={redirect} />}
      {isUserProfileOpen && <UserProfile />}
    </Container>
  )
};

export default Header;
