// Libs
import React, { useState } from 'react';
import styled from 'styled-components';

// Images
import CloseIcon from '../assets/x.svg';

//Styles
const Container = styled.div`
  margin-bottom: 2rem;  
  width: 20rem;
  height: 23rem;
  border: 1px solid #2EC4B6;
  border-radius: 8px;
  box-shadow: 5px 2px 10px 0px #2ec4b654;

  &:hover {
    border-color: #249b90;
    box-shadow: 5px 2px 10px 0px #249b90;
  }
`;

const Image = styled.div`
  width: 20rem;
  height: 16rem;
  background-color: red;

  p {
    margin: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    height: inherit;
  }
`;

const HeroName = styled.p`
  margin: 1rem;
  color: #2EC4B6;
  text-align: center;
  font-size: 1rem;
  font-weight: 600;
  word-break: break-all;
`;

const Overlay = styled.div`
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: rgba(196, 196, 196, 0.3);
  z-index: 2;
`;

const ContainerChoiceDetails = styled.div`
  margin-top: .5rem;
  width: 100%;
  display: flex;
  justify-content: center;
`;

const ChoiceDetailsItem = styled.span`
  margin: .2rem;
  padding: .5rem;
  color: #fff;
  background-color: #2EC4B6;
  border-radius: 15px;
  cursor: pointer;

  &:hover {
    background-color: #249b90;
  }
`;

const ContainerDetails = styled.div`
  position: relative;
  width: 35%;
  max-width: 35%;
  height: auto;
  background-color: #fff;
  border-radius: 10px;

  @media(max-width: 768px) {
    width: 60%;
    max-width: 60%;
  }

  @media(max-width: 648px) {
    width: 90%;
    max-width: 90%;
  }
`;

const DetailsTitle = styled.h2`
  margin: 1rem;
  text-align: center;
`;

const DetailsClose = styled.img`
  margin: .5rem;
  position: absolute;
  right: 0;
  width: 2rem;
  cursor: pointer;
`;

const ContainerDescription = styled.div`
  padding: 1rem;
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  align-items: baseline;

  @media(max-width: 648px) {
    padding: .5rem;
  }
`;

const DescriptionItem = styled.div`
  padding: 0 0.8rem 1.5rem;
  width: 30%;
  display: flex;
  justify-content: center;
  flex-direction: column;

  @media(max-width: 648px) {
    padding: 0 0.5rem 1.5rem;
    width: 32%;
  }
`;

const DescriptionText = styled.p`
  margin-bottom: ${(props) => props.title && '.2rem'};
  color: ${(props) => props.title ? '#2EC4B6' : '#000'};
  font-size: ${(props) => props.title ? '1.2rem' : '.9rem'};
  font-weight: ${(props) => props.title ? '600' : '500'};

  @media(max-width: 648px) {
    font-size: ${(props) => props.title ? '1rem' : '.80rem'};
  }
`;

function Card({ superHerosList }){
  const [handleAppearance, setHandleAppearance] = useState(false);
  const [handleBiography, setHandleBiography] = useState(false);
  const [handlePowerstats, setHandlePowerstats] = useState(false);

  const handleOpenPowerstats = () => {
    setHandlePowerstats(!handlePowerstats);
  }

  const handleOpenBiography = () => {
    setHandleBiography(!handleBiography);
  }

  const handleOpenAppearance = () => {
    setHandleAppearance(!handleAppearance);
  }

  console.log('superHerosList', superHerosList)

  const renderPowerstats = () => {
    return (
      <Overlay>
        <ContainerDetails>
          <DetailsClose
            src={CloseIcon}
            alt='close'
            onClick={handleOpenPowerstats}
          />
          <DetailsTitle>Powerstats</DetailsTitle>
          {superHerosList && superHerosList.powerstats.map(item => (
            <ContainerDescription>
            {console.log('item card', item)}
              <DescriptionItem>
                <DescriptionText title>combat</DescriptionText>
                <DescriptionText>descricao</DescriptionText>
              </DescriptionItem>
            </ContainerDescription>
          ))}
          {/* <ContainerDescription>
            <DescriptionItem>
              <DescriptionText title>combat</DescriptionText>
              <DescriptionText>descricao</DescriptionText>
            </DescriptionItem>
            <DescriptionItem>
              <DescriptionText title>combat</DescriptionText>
              <DescriptionText>descricao</DescriptionText>
            </DescriptionItem>
            <DescriptionItem>
              <DescriptionText title>combat</DescriptionText>
              <DescriptionText>descricao</DescriptionText>
            </DescriptionItem>
            <DescriptionItem>
              <DescriptionText title>combat</DescriptionText>
              <DescriptionText>descricao</DescriptionText>
            </DescriptionItem>
            <DescriptionItem>
              <DescriptionText title>combat</DescriptionText>
              <DescriptionText>descricao</DescriptionText>
            </DescriptionItem>
            <DescriptionItem>
              <DescriptionText title>combat</DescriptionText>
              <DescriptionText>descricao</DescriptionText>
            </DescriptionItem>
          </ContainerDescription> */}
        </ContainerDetails>
      </Overlay>
    )
  }

  const renderBiography = () => {
    return (
      <Overlay>
        <ContainerDetails>
          <DetailsClose
            src={CloseIcon}
            alt='close'
            onClick={handleOpenBiography}
          />
          <DetailsTitle>Biography</DetailsTitle>
          <ContainerDescription>
            <DescriptionItem>
              <DescriptionText title>Aliases</DescriptionText>
              <DescriptionText>Metron</DescriptionText>
            </DescriptionItem>
            <DescriptionItem>
              <DescriptionText title>Alignment</DescriptionText>
              <DescriptionText>Metron</DescriptionText>
            </DescriptionItem>
            <DescriptionItem>
              <DescriptionText title>Alter-egos</DescriptionText>
              <DescriptionText>Metron</DescriptionText>
            </DescriptionItem>
            <DescriptionItem>
              <DescriptionText title>First Appearance</DescriptionText>
              <DescriptionText>Metron</DescriptionText>
            </DescriptionItem>
            <DescriptionItem>
              <DescriptionText title>Full-name</DescriptionText>
              <DescriptionText>Metron</DescriptionText>
