// Libs
import React from 'react';
import styled from 'styled-components';

// Images
import loaderWhite from '../assets/loader-white.svg';

// Styles
const Default = styled.button`
  margin-top: 1rem;
  width: 65%;
  height: 3rem;

  display: flex;
  justify-content: center;
  align-items: center;

  color: #fff;
  font-size: .95rem;
  border-radius: 10px;
  border: none;
  background: ${(props) => props.background};
  cursor: pointer;
`;

const Text = styled.p`
  color: ${(props) => props.color};
  margin-right: .5rem;
`;

const Button = ({ background, onClick, color, text, isFetching }) => (
  <Default
    background={background}
    onClick={onClick}
  >
    <Text color={color}>
      {text}
    </Text>
    {isFetching ? <img src={loaderWhite} alt="loading..." /> : null}
  </Default>
);

Button.defaultProps = {
  text: 'hello world',
  color: '#fff',
  background: '#2EC4B6'
};

export default Button;
