// Libs
import React, { Component } from 'react';
import styled from 'styled-components';

// Components
import Header from '../components/Header';

// Styles
const Container = styled.div`
  height: 100vh;
  font-family: 'Overpass', Regular;
  background: #f2f4f7;
`;

class Dashboard extends Component {
  state = {
    user: {
      name: '',
      surname: ''
    }
  }

  handleSubmit = () => {

  }

  handleChange = (ev, field) => {
    const { user } = this.state;

    user[field] = ev.target.value;

    if (field === 'name') {
      console.log('name')

    }

    if (field === 'surname') {
      console.log('surname')
    }

    this.setState({
      user,
    })
  }

  render() {
    const { user } = this.state;

    console.log('user.name', user)

    return (
      <Container>
        <Header />
        <form onSubmit={this.handleSubmit}>
          <div>
            <p>nome</p>
            <input value={user.name} type="text" onChange={(ev) => this.handleChange(ev, 'name')} />
          </div>
          <div>
            <p>sobrenome</p>
            <input value={user.surname} type="text" onChange={(ev) => this.handleChange(ev, 'surname')} />
        </div>
        <button>enviar</button>
        </form>

      </Container>
    )
  }
}

export default Dashboard;
