// Libs
import React, { Component } from 'react';
import styled from 'styled-components';

// Components
import Header from '../components/Header';
import Input from '../components/Input';
import Button from '../components/Button';

// Images
import editIcon from '../assets/edit.svg';
import deleteIcon from '../assets/x-circle.svg';

// Styles
const Container = styled.div`
  height: 100vh;
  font-family: 'Overpass', Regular;
  background: #f2f4f7;
`;

const ContainerTodo = styled.div`
  margin: 2rem;
  padding-bottom: 1rem;
  width: 25%;
  max-width: 35%;
  border: 2px solid #c7cad0;
  border-radius: 10px;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  text-align: center;
`;

const ContentTodo = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ContainerList = styled.ul`
  margin: 2rem;
  padding: 0;
  width: 60%;
  max-width: 60%;
  border: 2px solid #c7cad0;
  border-radius: 10px;
`;

const ListItem = styled.li`
  padding: 1rem;
  list-style: none;
  border-bottom: 1px solid #c7cad0;
`;

class Dashboard extends Component {

  renderList = () => {
    return (
      <ContainerList>
        <ListItem>
          <p>aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa</p>
          <img src={editIcon} alt="editar" />
        </ListItem>
      </ContainerList>
    );
  }

  render() {
    return (
      <Container>
        <Header />
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <ContainerTodo>
            <Title>A Fazer</Title>
            <ContentTodo>
              <Input
                type="text"
                label="Titulo"
                containerWidth="75%"
                inputWidth="100%"
                borderBottom="2px solid #d7d9dc"
              />
              <Input
                type="text"
                label="Tarefa"
                containerWidth="75%"
                inputWidth="100%" 
                borderBottom="2px solid #d7d9dc"
              />
              <Button 
                text="Adicionar"
              />
            </ContentTodo>
          </ContainerTodo>
            {this.renderList()}
        </div>
      </Container>
    )
  }
}

export default Dashboard;
