// Libs
import React, { Component, Fragment } from 'react';
import styled from 'styled-components';

// Images
import bg from '../assets/onboardingp.jpeg';

// Components
import Input from '../components/Input';

// Services

// Styles

const Container = styled.div`
  display: flex;
  height: 100vh;
`;

const ContainerBg = styled.aside`
  background-image: url(${bg});
  background-position: bottom;
  width: 50%;
`;

const Content = styled.div`
  width: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const ContainerAccount = styled.form`
  width: 75%;
  display: flex;
  flex-direction: column;
  align-self: flex-end;
`;

const Title = styled.h1`
  margin-bottom: 3rem;
  font-size: 2.5rem;
`;

const ButtonSubmit = styled.button`
  margin-top: 1rem;
  width: 65%;
  height: 3rem;
  color: #fff;
  font-size: .95rem;
  border-radius: 10px;
  border: none;
  background: #2EC4B6;
  cursor: pointer;
`;

const AnotherOptionText = styled.p`
  margin-top: 2rem;
  width: fit-content;

  span {
    color: #2EC4B6;
    cursor: pointer;
  }
`;

class Onboarding extends Component {
  state = {
    isCreateAccount: false,
  };

  handleCreateAccount = () => {
    console.log('create account');
    this.setState({
      isCreateAccount: !this.state.isCreateAccount,
    });
  }

  render() {
    const { isCreateAccount } = this.state;

    return (
      <Container>
        <Content>
          <ContainerAccount>
            <Title>{isCreateAccount ? 'Criar Conta' : 'Login'}</Title>
              <Input
                type="email" 
                label="E-mail"
                placeholder="você@exemplo.com"
              />
              <Input
                type="password" 
                label="Senha"
                placeholder="Insira a sua senha"
              />
              {isCreateAccount && (
                <Input
                  type="password" 
                  label="Repita a Senha"
                  placeholder="Repita a sua senha"
                />
              )}
              <ButtonSubmit>
                {isCreateAccount ? 'Criar Conta' : 'Login'}
              </ButtonSubmit>
            <AnotherOptionText>
              {isCreateAccount ? (
                <Fragment>
                  Você já possui uma conta? <span onClick={this.handleCreateAccount}>Faça Login</span>
                </Fragment>
              ) : (
                <Fragment>
                  Você ainda não possui uma conta? <span onClick={this.handleCreateAccount}>Crie agora</span>
                </Fragment>
              )}
            </AnotherOptionText>
          </ContainerAccount>
        </Content>
        <ContainerBg>
          {/* <p>background</p> */}
        </ContainerBg>
      </Container>
    )
  }
}

export default Onboarding;
