// Libs
import React, { useState } from 'react';
import styled from 'styled-components';
import { NavLink, Redirect } from 'react-router-dom';

// Images
import apertureIcon from '../assets/aperture.svg';
import menuIcon from '../assets/menu.svg';
import userIcon from '../assets/user.svg';
import logoutIcon from '../assets/log-out.svg';

// Styles
const Container = styled.div`
  padding: 1rem 0;
  width: 100%;
  height: 4rem;
  display: flex;
  justify-content: center;
`;

const Content = styled.div`
  padding: 0 1rem;
  width: 95%;
  height: 4rem;
  display: flex;
  align-items: center;
  border: 1px solid #e7e9eb;
  border-radius: 20px;
  background: #fff;
`;

const ContainerLinks = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
`;

const Link = styled(NavLink)`
  margin-right: 2rem;
  color: #000;
  text-decoration: none;
`;

const MenuIcon = styled.img`
  position: relative;
  width: 1.5rem;
  cursor: pointer;
`;

const ContainerMenu = styled.div`
  position: absolute;
  top: 4rem;
  right: 0;
  display: flex;
  flex-direction: column;
  background: #afafb2;
`;

const ContainerUser = styled.div`
  display: flex;
`;

const Text = styled.p`
  margin: .5rem;
  color: #fff;
`;

const ContainerLogout = styled.div`
  display: flex;
`;

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleOpenMenu = () => {
    setIsMenuOpen(!isMenuOpen);

    console.log('isMenuOpen', isMenuOpen)
  }

  const renderMenu = () => (
    <ContainerMenu>
      <span>
        <ContainerUser>
          <img src={userIcon} alt="user" />
          <Text>Yasmin</Text>
        </ContainerUser>
      </span>
      <span>
        <ContainerLogout>
          <img src={logoutIcon} alt="logout" />
          <Text>Sair</Text>
        </ContainerLogout>
      </span>
    </ContainerMenu>
  );

  const renderLinks = () => (
    <ContainerLinks>
      <Link
        exact to="/dashboard"
        activeClassName="button-header-dash"
      >
        Dashboard
      </Link>
      <Link
        exact to="/about"
        activeClassName="button-header-dash"
      >
        Sobre
      </Link>
      <Link
        exact to="/search"
        activeClassName="button-header-dash"
      >
        Pesquisar
      </Link>
    </ContainerLinks>
  );

  return (
    <Container>
      <Content>
        <img src={apertureIcon} alt="aperture" />
        {renderLinks()}
        <MenuIcon src={menuIcon} alt="menu" onClick={handleOpenMenu} />
        {/* <ContainerUser>
          <img src={userIcon} alt="user" />
          <Text>Yasmin</Text>
        </ContainerUser> */}
        {/* <ContainerLogout>
          <Text>Sair</Text>
          <img src={logoutIcon} alt="logout" />
        </ContainerLogout> */}
      </Content>
      {isMenuOpen && renderMenu()}
    </Container>
  )
};

export default Header;
