    //   });
    default: return state;
  }
}

// Action Creator
export const addHero = info => ({
  type: ADD_HERO,
  info,
});

export const editTask = info => ({
  type: EDIT_TASK,
  info,
});

export const deleteTask = info => ({
  type: DELETE_TASK,
  info,
})
