// Libs
import React, { useState } from 'react';
import styled from 'styled-components';

//Styles
const Container = styled.div`
  margin-bottom: 2rem;  
  width: 20rem;
  height: 22rem;
  border: 1px solid;
  border-radius: 8px;
  box-shadow: 5px 2px 10px 0px #2121213d;
`;

const Image = styled.div`
  width: 20rem;
  height: 18rem;
  background-color: red;

  p {
    margin: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    height: inherit;
  }
`;

const HeroName = styled.p`
  text-align: center;
  font-size: 1rem;
  font-weight: 600;
  word-break: break-all;
`;

function Card(){
  const [handleDetails, setHandleDetails] = useState(false);

