// Libs
import React, { Component } from 'react';
import styled from 'styled-components';

// Images
import bg from '../assets/onboardingp.jpeg';

// Components
import Input from '../components/Input';

// Services

// Styles

const Container = styled.div`
  display: flex;
  height: 100vh;
`;

const ContainerBg = styled.aside`
  background-image: url(${bg});
  background-position: bottom;
  width: 40%;
`;

const Content = styled.div`
  width: 60%;
`;

const ContainerAccount = styled.div`
`;


class Onboarding extends Component {
  render() {
    return (
      <Container>
        <Content>
          <ContainerAccount>
            <h1>Log in</h1>
            <div>
              <Input type="email" />
              <Input type="password" />
            </div>
          </ContainerAccount>
        </Content>
        <ContainerBg>
          {/* <p>background</p> */}
        </ContainerBg>
      </Container>
    )
  }
}

export default Onboarding;
