// Libs
import React, { Component } from 'react';
import styled from 'styled-components';

// Components
import Header from '../components/Header';
import Input from '../components/Input';
import Button from '../components/Button';

// Styles
const Container = styled.div`
  height: 100vh;
  font-family: 'Overpass', Regular;
  background: #f2f4f7;
`;

const ContainerTodo = styled.div`
  margin: 1.7rem;
  padding-bottom: 1rem;
  width: 25rem;
  max-width: 35%;
  border: 2px solid #c7cad0;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  text-align: center;
`;

const ContentTodo = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

class Dashboard extends Component {
  render() {
    return (
      <Container>
        <Header />
        <ContainerTodo>
          <Title>A Fazer</Title>
          <ContentTodo>
            <Input
              type="text"
              label="Titulo"
              containerWidth="75%"
              inputWidth="100%"
              borderBottom="2px solid #d7d9dc"
            />
            <Input
              type="text"
              label="Tarefa"
              containerWidth="75%"
              inputWidth="100%" 
              borderBottom="2px solid #d7d9dc"
            />
            <Button 
              text="Adicionar"
            />
          </ContentTodo>
        </ContainerTodo>
      </Container>
    )
  }
}

export default Dashboard;
