// Libs
import React from 'react';
import styled from 'styled-components';

// Styles
const Default = styled.button`
  margin-top: 1rem;
  width: 65%;
  height: 3rem;
  color: #fff;
  font-size: .95rem;
  border-radius: 10px;
  border: none;
  background: ${(props) => props.background};
  cursor: pointer;
`;

const Text = styled.p`
  color:  ${(props) => props.color};
`;

const Button = (props) => (
  <Default background={props.background} onClick={props.onClick}>
    <Text color={props.color}>
      {props.text}
    </Text>
  </Default>
);

Button.defaultProps = {
  text: 'hello world',
  color: '#fff',
  background: '#2EC4B6'
};

export default Button;
