// Libs
import React, { Component } from 'react';
import styled from 'styled-components';

// Components
import Header from '../components/Header';
import Input from '../components/Input';
import Button from '../components/Button';

// Images
import editIcon from '../assets/edit.svg';
import deleteIcon from '../assets/x-circle.svg';

// Styles
const Container = styled.div`
  height: 100vh;
  font-family: 'Overpass', Regular;
  background: #f2f4f7;
`;

const ContainerTodo = styled.div`
  margin: 2rem;
  padding-bottom: 1rem;
  width: 25%;
  max-width: 35%;
  border: 2px solid #c7cad0;
  border-radius: 10px;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  text-align: center;
`;

const ContentTodo = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ContainerList = styled.ul`
  margin: 2rem;
  padding: 0;
  width: 60%;
  max-width: 60%;
  border: 2px solid #c7cad0;
  border-radius: 10px;
`;

const ListItem = styled.li`
  padding: 1rem;
  display: flex;
  justify-content: space-between;
  list-style: none;
  border-bottom: 1px solid #c7cad0;

  &:hover {
		background: #c7cad088;
	}
`;

const ListItemText = styled.p`
  width: 90%;
  font-size: 1rem;
  word-break: break-all;
`;

const ListItemOptions = styled.div`
  display: flex;
  align-items: center;
`;

const ItemOptionsIcon = styled.img`
  margin-right: ${(props) => props.first && '.5rem'};
  cursor: pointer;
`;

class Dashboard extends Component {
  state = {
    values: {}
  }

  handleSubmit = () => {
    const { title, task } = this.state.values;
    if (title.length > 0 && task.length > 0) {
      console.log('maior heinnnn')
    } else {
      console.log('MENOR heinnnn')
    }
  }

  handleChange = (ev, field) => {
    const { values } = this.state;

    values[field] = ev.target.value;

    console.log('values', this.state.values)

    this.setState({
      values,
    });
  }

  renderList = () => {
    return (
      <ContainerList>
        <ListItem>
          <ListItemText>aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa</ListItemText>
          <ListItemOptions>
            <ItemOptionsIcon first src={editIcon} alt="editar" />
            <ItemOptionsIcon src={deleteIcon} alt="excluir" />
          </ListItemOptions>
        </ListItem>
      </ContainerList>
    );
  }

  render() {
    const { titleValue, taskValue } = this.state;

    return (
      <Container>
        <Header />
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <ContainerTodo>
            <Title>A Fazer</Title>
            <ContentTodo>
              <Input
                text={titleValue}
                type="text"
                label="Titulo"
                containerWidth="75%"
                inputWidth="100%"
                borderBottom="2px solid #d7d9dc"
                onChange={(ev) => this.handleChange(ev, 'title')}
              />
              <Input
                text={taskValue}
                type="text"
                label="Tarefa"
                containerWidth="75%"
                inputWidth="100%" 
                borderBottom="2px solid #d7d9dc"
                onChange={(ev) => this.handleChange(ev, 'task')}
              />
              <Button 
                text="Adicionar"
                onClick={this.handleSubmit}
              />
            </ContentTodo>
          </ContainerTodo>
            {this.renderList()}
        </div>
      </Container>
    )
  }
}

export default Dashboard;
