// Libs
import React, { Component } from 'react';
import styled from 'styled-components';
import { nanoid } from "nanoid";
import { connect } from 'react-redux';

// Components
import Header from '../components/Header';
import Input from '../components/Input';
import Button from '../components/Button';

// Images
import editIcon from '../assets/edit.svg';
import deleteIcon from '../assets/x-circle.svg';

// Redux
import { addTask, deleteTask, editTask } from '../dataflow/modules/todo-modules';

const mapStateToProps = (state) => ({
  list: state.todo.list,
})

const mapDispatchToProps = (dispatch) => ({
  addTask: (info) => dispatch(addTask(info)),
  deleteTask: (info) => dispatch(deleteTask(info)),
  editTask: (info) => dispatch(editTask(info)),
});

// Styles
const Container = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
`;

const Content = styled.div`
  width: 80%;
  display: flex;

  height: 100vh;
  font-family: 'Overpass', Regular;
  background: #f2f4f7;
`;

const Wrapper = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
`;

const ContainerTodo = styled.div`
  margin: 2rem;
  padding-bottom: 1rem;
  width: 25%;
  max-width: 35%;
  height: fit-content;
  border: 2px solid #c7cad0;
  border-radius: 10px;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  text-align: center;
`;

const ContentTodo = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ContainerList = styled.ul`
  margin: 2rem;
  padding: 0;
  width: 60%;
  max-width: 60%;
  height: 80vh;
  border: 2px solid #c7cad0;
  border-radius: 10px;
  display: ${(props) => props.emptyMessage && 'flex'};
  justify-content: ${(props) => props.emptyMessage && 'center'};
  align-items: ${(props) => props.emptyMessage && 'center'};
`;

const ListItem = styled.li`
  padding: 1rem;
  display: flex;
  justify-content: space-between;
  list-style: none;
  border-bottom: 1px solid #c7cad0;

  &:hover {
		background: #c7cad088;
	}
`;

const ListItemText = styled.p`
  margin-right: .2rem;
  width: 90%;
  color: ${(props) => props.category ? '#2EC4B6' : '#000'};
  font-size: 1rem;
  word-break: break-all;
  font-weight: ${(props) => (props.name || props.category) && '800'};
`;

const ListItemOptions = styled.div`
  display: flex;
  align-items: center;
`;

const ItemOptionsIcon = styled.img`
  margin-right: ${(props) => props.first && '.5rem'};
  cursor: pointer;
`;

const EmptyListMessage = styled.p`
  font-size: 1.5rem;
  text-align: center;
`;

const ErrorMessage = styled.p`
  margin: 0;
  font-size: .8rem;
  color: tomato;
  font-weight: bold;
`;

class SuperHeros extends Component {
  state = {
    values: {
      name: '',
      task: '',
      category: '',
    },
    nameError: undefined,
    taskError: undefined,
    categoryError: undefined,
    emptyError: undefined,
    list: [],
  }

  handleSubmit = () => {
    const { emptyError, values, list } = this.state;
    const { name, task, category, nameError, taskError, categoryError } = values;

    if (name === '' && task === '' && category === '') {
      this.setState({
        emptyError: 'Preencha todos os campos.',
      });
    } else {
      this.setState({
        emptyError: undefined,
      });

      if (name.length === 0) {
        this.setState({
          nameError: 'Insira um nome.',
        });
      } else {
        this.setState({
          nameError: undefined,
        })
      }

      if (task.length === 0) {
        this.setState({
          taskError: 'Insira uma marca.',
        });
      } else {
        this.setState({
          taskError: undefined,
        })
      }

      if (category.length === 0) {
        this.setState({
          categoryError: 'Insira uma ficha tÃ©cnica.',
        });
      } else {
        this.setState({
          categoryError: undefined,
        });
      }

      if (!emptyError && !nameError && !taskError && !categoryError) {
        const item = {id: nanoid(5), name, task, category}

        // Create
        if (!values.id) {
          this.setState({
            list: list.concat(item),
            editModel: false,
            values: {
              name: '',
              task: '',
              category: '',
            }
          });

          this.props.addTask(item);

        } else {
          // Edit

          //pegando a lista do estado
          // const selectedItem = list.find(item => item.id === values.id);

          const selectedItem = this.props.list.find(item => item.id === values.id);

          this.setState({
            values: selectedItem,
          });

          const newList = this.props.list.map(item => (item.id === values.id ? values : item));

          this.props.editTask(newList);

          this.setState({
            // list: newList,
            values: {
              name: '',
              task: '',
              category: '',
            },
            editModel: false,
          });
        }
      }
    }
  }

  handleChange = (ev, field) => {
    const { values } = this.state;

    values[field] = ev.target.value;

    this.setState({
      values,
    });
  }

  handleDeleteModel = (model) => {
    // delete via componente
    // const { list } = this.state;

    // const del = list.filter(item => item.id !== model.id);

    // this.setState({
    //   list: del,
    // });


    // delete via redux
    this.props.deleteTask(model);
  }

  handleEditModel = (model) => {
    this.setState({
      values: {
        id: model.id,
        name: model.name,
        task: model.task,
        category: model.category,
      },
      editModel: true,
    });
  }

  renderList = () => {
    const { list } = this.props;

    return (
      <ContainerList emptyMessage={list.length === 0}>
        {list.length > 0 ? (
          list.map(item => (
            <ListItem>
              <ListItemText name>{item.name}</ListItemText>
              <ListItemText>{item.task}</ListItemText>
              <ListItemText category>{item.category}</ListItemText>
              <ListItemOptions>
                <ItemOptionsIcon first src={editIcon} alt="editar" onClick={() => this.handleEditModel(item)} />
                <ItemOptionsIcon src={deleteIcon} alt="excluir" onClick={() => this.handleDeleteModel(item)} />
              </ListItemOptions>
            </ListItem>
          ))
        ) : <EmptyListMessage>Ops! Infelizmente nÃ£o hÃ¡ nenhum super-herÃ³i para mostrar :(</EmptyListMessage>
        }
      </ContainerList>
    );
  }

  render() {

    return (
      <Container>
        <Header />
        <Content>
          <Wrapper>
            <ContainerTodo>
              <Title>Super HerÃ³is</Title>

            </ContainerTodo>
            {this.renderList()}
          </Wrapper>
        </Content>
      </Container>
    )
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(SuperHeros);
