// Libs
import React from 'react';
import styled from 'styled-components';

// Images
import eyeOn from '../assets/eye.svg';
import eyeOff from '../assets/eye-off.svg';

// Styles
const Container = styled.div`
  width: ${(props) => props.containerWidth};
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  margin-bottom: .3rem;
  color: #011627;
  font-size: .85rem;
`;

const Wrapper = styled.div`
  width: ${(props) => props.pass && '65%'};
  display: ${(props) => props.pass && 'flex'};
  align-items: ${(props) => props.pass && 'center'};
  border-bottom: ${(props) => props.pass && '2px solid #eef1f2'};
`;

const DefaultInput = styled.input`
  margin-bottom: ${(props) => (!props.pass && !props.error) && '1rem'};
  padding: 1rem 0;
  width: ${(props) => props.inputWidth ? props.inputWidth : "65%"};
  border: none;
  border-bottom: ${(props) => props.borderBottom ? props.borderBottom : '2px solid #eef1f2'};
  background: transparent;

  @media(max-width: 648px) {
    width: ${(props) => props.inputWidthMob ? '95%' : '95%'};
  }
`;

const ShowPasswordIcon = styled.img`
  cursor: pointer;
`;

const Input = (props) => (
  <Container containerWidth={props.containerWidth}>
    <Label>{props.label}</Label>
    <Wrapper pass={props.pass}>
      <DefaultInput
        value={props.value}
        type={props.type}
        required={props.required}
        placeholder={props.placeholder}
        inputWidth={props.inputWidth}
        inputWidthMob={props.inputWidthMob}
        borderBottom={props.borderBottom}
        pass={props.pass}
        onChange={props.onChange}
        error={props.error}
      />
      {props.pass && (
        !props.showPass ? (
          <ShowPasswordIcon
            src={eyeOn}
            alt="" 
            onClick={props.handleShowPassword}
          />
        ) : (
          <ShowPasswordIcon
            src={eyeOff}
            alt="" 
            onClick={props.handleShowPassword}
          />
        )
      )}
    </Wrapper>
  </Container>
);

Input.defaultProps = {
	label: 'label',
	type: 'text',
	placeholder: 'Digite aqui...',
	required: true,
};

export default Input;
