  width: 100%;
  height: 95vh;
  overflow-y  : scroll;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-row-gap: 1px;
  align-items: center;

  @media(max-width: 768px) {
    padding: 0 3rem;
    grid-template-columns: repeat(1, 1fr);
  }
`;

const ScrollButton = styled.img`
