// Libs
import React, { useState } from 'react';
import styled from 'styled-components';

// Images
import CloseIcon from '../assets/x.svg';

//Styles
const Container = styled.div`
  margin-bottom: 2rem;  
  width: 20rem;
  height: 23rem;
  border: 1px solid;
  border-radius: 8px;
  box-shadow: 5px 2px 10px 0px #2121213d;

  &:hover {
    border-color: #2EC4B6;
    box-shadow: 5px 2px 10px 0px #2ec4b654;
  }
`;

const Image = styled.div`
  width: 20rem;
  height: 16rem;
  background-color: red;

  p {
    margin: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    height: inherit;
  }
`;

const HeroName = styled.p`
  margin: 1rem;
  text-align: center;
  font-size: 1rem;
  font-weight: 600;
  word-break: break-all;
`;

const Overlay = styled.div`
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  background: rgba(196, 196, 196, 0.3);
  z-index: 2;
`;

const ContainerChoiceDetails = styled.div`
  margin-top: .5rem;
  width: 100%;
  display: flex;
  justify-content: center;
`;

const ChoiceDetailsItem = styled.span`
  margin: .2rem;
  padding: .5rem;
  background-color: #2EC4B6;
  border-radius: 15px;
  cursor: pointer;

  &:hover {
    background-color: #249b90;
  }
`;

const ContainerDetails = styled.div`
  position: relative;
  width: 40%;
  height: 60vh;
  background-color: #fff;
`;

const DetailsTitle = styled.h2`
  margin: 1rem;
  text-align: center;
`;

const DetailsClose = styled.img`
  position: absolute;
  right: 0;
  width: 2rem;
  cursor: pointer;
`;

function Card(){
  const [handleAppearance, setHandleAppearance] = useState(false);
  const [handleBiography, setHandleBiography] = useState(false);
  const [handlePowerstats, setHandlePowerstats] = useState(false);

  const handleOpenPowerstats = () => {
    setHandlePowerstats(!handlePowerstats)
  }

  const handleOpenBiography = () => {
    setHandleBiography(!handleBiography)
  }

  const handleOpenAppearance = () => {
    setHandleAppearance(!handleAppearance)
  }

  const renderPowerstats = () => {
    return (
      <Overlay onClick={handleOpenPowerstats}>
        <ContainerDetails>
          <DetailsClose
            src={CloseIcon}
            alt='close'
            onClick={handleOpenPowerstats}
          />
          <DetailsTitle>Powerstats</DetailsTitle>
          <p>aaaaaaa</p>
          <p>aaaaaaa</p>
          <p>aaaaaaa</p>
        </ContainerDetails>
      </Overlay>
    )
  }

  const renderBiography = () => {
    return (
      <Overlay onClick={handleOpenBiography}>
        <ContainerDetails>
          <DetailsClose
            src={CloseIcon}
            alt='close'
            onClick={handleOpenBiography}
          />
          <DetailsTitle>Biography</DetailsTitle>
          <p>aaaaaaa</p>
          <p>aaaaaaa</p>
          <p>aaaaaaa</p>
        </ContainerDetails>
      </Overlay>
    )
  }

  const renderAppearance = () => {
    return (
      <Overlay onClick={handleOpenAppearance}>
        <ContainerDetails>
          <DetailsClose
            src={CloseIcon}
            alt='close'
            onClick={handleOpenAppearance}
          />
          <DetailsTitle>Appearance</DetailsTitle>
          <p>aaaaaaa</p>
          <p>aaaaaaa</p>
          <p>aaaaaaa</p>
        </ContainerDetails>
      </Overlay>
    )
  }

  return (
    <Container>
      <HeroName>name</HeroName>
      <Image>
        <p>imagem</p>
      </Image>
      <ContainerChoiceDetails>
        <ChoiceDetailsItem onClick={handleOpenAppearance}>Appearance</ChoiceDetailsItem>
        <ChoiceDetailsItem>Biography</ChoiceDetailsItem>
        <ChoiceDetailsItem>Powerstats</ChoiceDetailsItem>
      </ContainerChoiceDetails>
      {handleAppearance && renderAppearance()}
      {handleBiography && renderBiography()}
      {handlePowerstats && renderPowerstats()}
    </Container>
  )
}

export default Card;
