// Libs
import React, { Component } from 'react';
import styled from 'styled-components';

// Components
import Header from '../components/Header';

// Styles
const Container = styled.div`
  height: 100vh;
  font-family: 'Overpass', Regular;
  background: #f2f4f7;
`;

class Dashboard extends Component {
  state = {
    user: {
      name: '',
      surname: ''
    }
  }

  handleChange = (ev, field) => {
    const { user } = this.state;

    user[field] = ev.target.value;


  }

  render() {
    const { user } = this.state;

    return (
      <Container>
        <Header />
        <div>
          <p>nome</p>
          <input value={user.name} onChange={(ev) => this.handleChange(ev, 'name')} />
        </div>
        <div>
          <p>sobrenome</p>
          <input value={user.surname} onChange={(ev) => this.handleChange(ev, 'surname')} />
        </div>

      </Container>
    )
  }
}

export default Dashboard;
