// Libs
import React, { Component, Fragment } from 'react';
import styled from 'styled-components';

// Images
import background from '../assets/onboardingp.jpeg';

// Components
import Input from '../components/Input';
import Button from '../components/Button';

// Services
import { createUser, login } from '../services/api';

// Styles
const Container = styled.div`
  display: flex;
  height: 100vh;
  font-family: 'Overpass', Regular;
`;

const CreatorName = styled.p`
  padding-left: 1rem;
  position: fixed;
  bottom: 0;
  font-size: .9rem;
  font-weight: bold;
`;

const ContainerBg = styled.aside`
  background-image: url(${background});
  background-position: bottom;
  width: 50%;
`;

const Content = styled.div`
  width: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const Form = styled.form`
  width: 75%;
  display: flex;
  flex-direction: column;
  align-self: flex-end;
`;

const Title = styled.h1`
  margin-bottom: 3rem;
  font-size: 2.5rem;
`;

const AnotherOptionText = styled.p`
  margin-top: 2rem;
  width: fit-content;

  span {
    color: #2EC4B6;
    cursor: pointer;
  }
`;

const ErrorMessage = styled.p`
  color: tomato;
  font-size: .8rem;
  font-weight: 800;
`;

class Onboarding extends Component {
  state = {
    isCreateAccount: false,
    user: {
      username: '',
      email: '',
      password: '',
    },
    usernameError: undefined,
    emailError: undefined,
    passwordError: undefined,
  };

  handleCreateAccount = () => {
    this.setState({
      isCreateAccount: !this.state.isCreateAccount,
    });
  }

  handleChange = (ev, field) => {
    const { user } = this.state;

    user[field] = ev.target.value;

    if (field === 'username') {
      this.setState({
        usernameError: ev.target.value < 4,
      });
    }

    if (field === 'email') {
      this.setState({
        emailError: ev.target.value < 6,
      });
    }

    if (field === 'password') {
      this.setState({
        passwordError: ev.target.value < 6,
      });
    }

    this.setState({
      user,
    })
  }

  render() {
    const { isCreateAccount, user, usernameError, emailError, passwordError } = this.state;

    return (
      <Container>
        <CreatorName>Desenvolvido por: Yasmin Miranda</CreatorName>
        <Content>
          <Form>
            <Title>{isCreateAccount ? 'Criar Conta' : 'Login'}</Title>
              {isCreateAccount && (
                <Input
                  type="text"
                  label="Username"
                  placeholder="Insira o seu username"
                  value={user.username}
                  onChange={(ev) => this.handleChange(ev, 'username')}
                />
              )}
              {usernameError && <ErrorMessage>Insira um username válido.</ErrorMessage>}
              <Input
                type="email" 
                label="E-mail"
                placeholder="você@exemplo.com"
                value={user.email}
                onChange={(ev) => this.handleChange(ev, 'email')}
              />
              {emailError && <ErrorMessage>Insira um username válido.</ErrorMessage>}
              <Input
                type="password" 
                label="Senha"
                placeholder="Insira a sua senha"
                value={user.password}
                onChange={(ev) => this.handleChange(ev, 'password')}
              />
              {passwordError && <ErrorMessage>Insira um username válido.</ErrorMessage>}
              <Button 
                text={isCreateAccount ? 'Criar Conta' : 'Login'}
              />
            <AnotherOptionText>
              {isCreateAccount ? (
                <Fragment>
                  Você já possui uma conta? <span onClick={this.handleCreateAccount}>Faça Login</span>
                </Fragment>
              ) : (
                <Fragment>
                  Você ainda não possui uma conta? <span onClick={this.handleCreateAccount}>Crie agora</span>
                </Fragment>
              )}
            </AnotherOptionText>
          </Form>
        </Content>
        <ContainerBg>
          {/* <p>background</p> */}
        </ContainerBg>
      </Container>
    )
  }
}

export default Onboarding;
