// Libs
import React from 'react';
import styled from 'styled-components';
import { NavLink, Redirect } from 'react-router-dom';

// Images
import aperture from '../assets/aperture.svg';
import userIcon from '../assets/user.svg';

// Styles
const Container = styled.div`
  padding: 1rem 0;
  width: 100%;
  height: 4rem;
  display: flex;
  justify-content: center;
`;

const Content = styled.div`
  padding: 0 1rem;
  width: 95%;
  height: 4rem;
  display: flex;
  align-items: center;
  border: 1px solid #e7e9eb;
  border-radius: 20px;
  background: #fff;
`;

const ContainerLinks = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
`;

const Link = styled(NavLink)`
  margin-right: 2rem;
  color: #000;
  text-decoration: none;
`;

const ContainerUser = styled.div`
  display: flex;
`;

const Header = () => {
  const renderMenu = () => (
    <ContainerLinks>
      <Link
        exact to="/dashboard"
        activeClassName="button-header-dash"
      >
        Dashboard
      </Link>
      <Link
        exact to="/about"
        activeClassName="button-header-dash"
      >
        Sobre
      </Link>
      <Link
        exact to="/search"
        activeClassName="button-header-dash"
      >
        Pesquisar
      </Link>
    </ContainerLinks>
  );

  return (
    <Container>
      <Content>
        <img src={aperture} alt="aperture" />
        {renderMenu()}
        <ContainerUser>
          <img src={userIcon} alt="user" />
          <p>Yasmin</p>
        </ContainerUser>
      </Content>
    </Container>
  )
};

export default Header;
