// Libs
import React, { useState } from 'react';
import styled from 'styled-components';
import { NavLink, Redirect } from 'react-router-dom';

// Components
import UserProfile from './UserProfile';

// Images
import apertureIcon from '../assets/aperture.svg';
import menuOpenIcon from '../assets/menu.svg';
import menuCloseIcon from '../assets/x.svg';
import userIcon from '../assets/user.svg';
import logoutIcon from '../assets/log-out.svg';

// Styles
const Container = styled.div`
  padding: 1rem 0;
  width: 100%;
  height: 4rem;
  display: flex;
  justify-content: center;
  font-family: 'Overpass', Regular;
`;

const Content = styled.div`
  padding: 0 1rem;
  width: 95%;
  height: 4rem;
  display: flex;
  align-items: center;
  border: 1px solid #e7e9eb;
  border-radius: 20px;
  background: #fff;
`;

const ContainerLinks = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
`;

const Link = styled(NavLink)`
  margin-right: 2rem;
  color: #000;
  text-decoration: none;
`;

const MenuIcon = styled.img`
  position: relative;
  width: 1.5rem;
  cursor: pointer;
`;

const ContainerMenu = styled.div`
  position: absolute;
  top: 4.5rem;
  right: 0;
  margin-right: 1rem;
  display: flex;
  flex-direction: column;
  border-radius: 10px;
  background: #afafb2;
`;

const ContainerMenuItem = styled.div`
  display: flex;
  padding: 1rem 2rem;
  border-bottom: ${(props) => props.user && '0.1px solid #fff'};
  cursor: pointer;
`;

const Text = styled.p`
  margin: .5rem;
  color: #fff;
`;

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [redirect, setRedirect] = useState(undefined);
  const [isUserProfileOpen, setUserProfileOpen] = useState(false);

  const handleOpenMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  }

  const handleLogout = () => {
    localStorage.removeItem('sessionToken');

    setRedirect('/');
  }

  const handleUserProfile = () => {
    setUserProfileOpen(true);
  }

  const renderMenu = () => {
    const dataName = localStorage.getItem('username');
    const username = dataName[0].toUpperCase() + dataName.slice(1).toLowerCase();

    return (
      <ContainerMenu>
        <ContainerMenuItem user onClick={handleUserProfile}>
          <img src={userIcon} alt="user" />
          <Text>{username}</Text>
        </ContainerMenuItem>
        <ContainerMenuItem onClick={handleLogout}>
          <img src={logoutIcon} alt="logout" />
          <Text>Sair</Text>
        </ContainerMenuItem>
      </ContainerMenu>
    );
  };

  const renderLinks = () => (
    <ContainerLinks>
      <Link
        exact to="/dashboard"
        activeClassName="button-header-dash"
      >
        Dashboard
      </Link>
      <Link
        exact to="/todo"
        activeClassName="button-header-dash"
      >
        To-do
      </Link>
      <Link
        exact to="/about"
        activeClassName="button-header-dash"
      >
        Sobre
      </Link>
      <Link
        exact to="/gallery"
        activeClassName="button-header-dash"
      >
        Galeria
      </Link>
    </ContainerLinks>
  );

  return (
    <Container>
      <Content>
        <img src={apertureIcon} alt="aperture" />
        {renderLinks()}
        {isMenuOpen ? (
          <MenuIcon src={menuCloseIcon} alt="close menu" onClick={handleOpenMenu} />
        ) : (<MenuIcon src={menuOpenIcon} alt="open menu" onClick={handleOpenMenu} />)}
      </Content>
      {isMenuOpen && renderMenu()}
      {redirect && <Redirect to={redirect} />}
      {isUserProfileOpen && <UserProfile />}
    </Container>
  )
};

export default Header;
