// Libs
import React, { Component, Fragment } from 'react';
import styled from 'styled-components';
import { connect } from 'react-redux';
import { Redirect } from 'react-router-dom';

// Images
import background from '../assets/onboardingp.jpeg';
import loaderWhite from '../assets/loader-white.svg';

// Components
import Input from '../components/Input';
import Button from '../components/Button';

// Services
import { createUser, login } from '../services/api';
import { newUser } from '../dataflow/modules/onboarding-modules';

const mapDispatchToProps = dispatch => ({
	newUser: (user) => {
		dispatch(newUser(user));
	},
});

// Styles
const Container = styled.div`
  display: flex;
  height: 100vh;
  font-family: 'Overpass', Regular;
`;

const CreatorName = styled.p`
  padding-left: 1rem;
  position: fixed;
  bottom: 0;
  font-size: .9rem;
  font-weight: bold;
`;

const ContainerBg = styled.aside`
  background-image: url(${background});
  background-position: bottom;
  width: 50%;
`;

const Content = styled.div`
  width: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const Form = styled.form`
  width: 75%;
  display: flex;
  flex-direction: column;
  align-self: flex-end;
`;

const Title = styled.h1`
  margin-bottom: 3rem;
  font-size: 2.5rem;
`;

const AnotherOptionText = styled.p`
  margin-top: 2rem;
  width: fit-content;

  span {
    color: #2EC4B6;
    cursor: pointer;
  }
`;

const ErrorMessage = styled.p`
  color: tomato;
  font-size: .8rem;
  font-weight: 800;
`;

class Onboarding extends Component {
  state = {
    isCreateAccount: false,
    user: {
      username: '',
      email: '',
      password: '',
    },
    usernameError: false,
    emailError: false,
    passwordError: false,
    redirect: undefined,
    showPassword: false,
    error: undefined,
    isLoading: false,
  };


  createUserAccount = async (user) => {
    try {
      const response = await createUser(user);

      console.log('response create user api', response)

    } catch (error) {
      console.log('error', error)
    }
  }

  UserLogin = async (user) => {
    try {
      const response = await login(user.email, user.password);

      if (!response) {
        this.setState({
          isLoading: true,
        });
      } else {
        this.setState({
          isLoading: false,
        });

        const deconstructing = response.data;

        localStorage.setItem('sessionToken', deconstructing.sessionToken);
        localStorage.setItem('username',  deconstructing.username);

        this.setState({
          redirect: '/dashboard',
        });
      }
    } catch (error) {
      console.log('error', error)
      console.log('error response', error.response)

      const ErrorMessage = error.response.data.error;

      if (ErrorMessage === 'Invalid username/password.') {
        this.setState({
          error: 'Username/Senha inválidos.',
        });
      }
    }
  }

  handleCreateAccount = () => {
    this.setState({
      isCreateAccount: !this.state.isCreateAccount,
    });
  }

  handleSubmit = (event) => {
    const { user, isCreateAccount, usernameError, emailError, passwordError } = this.state;

    event.preventDefault();

    if (!usernameError && !emailError && !passwordError) {
      if (!isCreateAccount) {
        delete user.username;

        this.UserLogin(user);
      } else {
        this.createUserAccount(user);
        this.props.newUser(user);
      }
    }
  }

  handleChange = (field, ev) => {
    const { user } = this.state;

    user[field] = ev.target.value;

    if (field === 'username') {
      this.setState({
        usernameError: ev.target.value.length < 4,
      });
    }

    if (field === 'email') {
      this.setState({
        emailError: ev.target.value.length < 6,
      });
    }

    if (field === 'password') {
      this.setState({
        passwordError: ev.target.value.length < 6,
      });
    }

    this.setState({
      user,
    })
  }

  handleShowPassword = () => {
    this.setState({
      showPassword: !this.state.showPassword,
    });
  }

  render() {
    const { isCreateAccount, user, usernameError, emailError, passwordError, redirect, error } = this.state;

    return (
      <Container>
        <CreatorName>Desenvolvido por Yasmin Miranda</CreatorName>
        <Content>
          <Form onSubmit={this.handleSubmit}>
            <Title>{isCreateAccount ? 'Criar Conta' : 'Login'}</Title>
              {isCreateAccount && (
                <Input
                  type="text"
                  label="Username"
                  placeholder="Insira o seu username"
                  value={user.username}
                  onChange={(ev) => this.handleChange('username', ev)}
                />
              )}
              {usernameError && <ErrorMessage>Insira um username válido.</ErrorMessage>}
              <Input
                type="email" 
                label="E-mail"
                placeholder="você@exemplo.com"
                value={user.email}
                onChange={(ev) => this.handleChange('email', ev)}
              />
              {emailError && <ErrorMessage>Insira um e-mail válido.</ErrorMessage>}
                <Input
                  type={this.state.showPassword ? 'text' : 'password'}
                  label="Senha"
                  placeholder="Insira a sua senha"
                  value={user.password}
                  onChange={(ev) => this.handleChange('password', ev)}
                  pass={true}
                  inputWidth="92%"
                  borderBottom="transparent"
                  showPass={this.state.showPassword}
                  handleShowPassword={this.handleShowPassword}
                />
              {passwordError && <ErrorMessage>Insira uma senha válida.</ErrorMessage>}
              {error && <ErrorMessage>{error}</ErrorMessage>}
              <Button 
                text={isCreateAccount ? 'Criar Conta' : 'Login'}
              />
            <AnotherOptionText>
              {isCreateAccount ? (
                <Fragment>
                  Você já possui uma conta? <span onClick={this.handleCreateAccount}>Faça Login</span>
                </Fragment>
              ) : (
                <Fragment>
                  Você ainda não possui uma conta? <span onClick={this.handleCreateAccount}>Crie agora</span>
                </Fragment>
              )}
            </AnotherOptionText>
          </Form>
        </Content>
        <ContainerBg>
          {/* <p>background</p> */}
        </ContainerBg>
        {redirect && <Redirect exact to={redirect} />}
      </Container>
    )
  }
}

export default connect(null, mapDispatchToProps)(Onboarding);
