// Action Creator
const ADD_TASK = 'simple/ADD/ADD_TASK';
const DELETE_TASK = 'simple/DELETE/DELETE_TASK';

const initialState = {
  list: [],
};

export default function Reducer(state = initialState, action) {
  switch(action.type) {
    case ADD_TASK:
      return Object.assign({}, state, {
        list: [
          ...state.list, action.info
        ]
      });
    case DELETE_TASK: 
      return Object.assign({}, state, {
        list: [state.list.filter(item => item.id !== action.info)]
      });
    default: return state;
  }
}

// ACTION TYPE
export const addTask = info => ({
  type: ADD_TASK,
  info,
})
