// Libs
import React, { Component } from 'react';
import styled from 'styled-components';

// Components
import Header from '../components/Header';

// Images
import scrollTopIcon from '../assets/scrollTop.svg';
import loaderIcon from '../assets/loader.svg';

// Services
import { getAllPics } from '../services/api';

// Styles
const Container = styled.div`
  width: 100%;
  min-height: 100vh;
  background: #f2f4f7;
  font-family: 'Overpass', Regular;
`;

const Content = styled.div`
  width: 100%;
  padding-top: ${(props) => !props.showList && '10rem'};
  display: ${(props) => props.showList ? 'grid' : 'flex'};
  justify-content: ${(props) => !props.showList && 'center'};
  grid-template-columns: ${(props) => props.showList && 'repeat(3, 1fr)'};
`;

const ContainerImage = styled.div`
  padding: 1rem;
  display: flex;
  justify-content: center;
  align-self: center;
`;

const Button = styled.img`
  position: fixed;
  bottom: 0;
  right: 0;
  margin-right: 1rem;
  margin-bottom: 1rem;
  padding: 1rem;
  display: ${(props) => props.display ? 'block' : 'none'};
  width: 2.5rem;
  cursor: pointer;
`;

const Loader = styled.img`
  width: 2rem;
  display: flex;
  align-self: center;
`;

class Search extends Component {
  state = {
    picsList: [],
    isButtonShowing: false,
  }

  componentDidMount() {
    this.fetchingData();

    window.onscroll = () => this.scrollFunction();
  }

  fetchingData = async () => {
    try {
      const response = await getAllPics();

      // this.setState({
      //   picsList: response.data,
      // });
    } catch(error) {
      console.log('error', error);
    }
  }

  scrollFunction = () => {
    if (document.body.scrollTop > 20 || document.documentElement.scrollTop > 20) {
      this.setState({ 
        isButtonShowing: true,
      });
    } else {
      this.setState({
        isButtonShowing: false,
      });
    }
  }

  handleTopWindow = () => {
    document.body.scrollTop = 0;
    document.documentElement.scrollTop = 0;
  }

  render() {
    return (
      <Container>
        <Header />
        <Content showList={this.state.picsList.length > 0}>
          {/* {this.state.picsList.length > 0 ? (
            <>
              {this.state.picsList.map((item, index) => (
                <ContainerImage key={index}>
                  <img src={item.urls.small} alt={item.alt_description} />
                </ContainerImage> 
              ))}
              <Button
                src={scrollTopIcon} 
                display={this.state.isButtonShowing} 
                onClick={this.handleTopWindow}
              >
              </Button>
            </>
          ) : (
            <Loader src={loaderIcon} alt="loader" />
          )} */}
          <Loader src={loaderIcon} alt="loader" />
        </Content>
      </Container>
    );
  }
}

export default Search;
