// Libs
import {
	compose, createStore, applyMiddleware, combineReducers,
} from 'redux';
import thunkMiddleware from 'redux-thunk';

// Reducers
import Onboarding from './dataflow/modules/onboarding-modules';
import Heroes from './dataflow/modules/heroes-modules';

// const reducers = combineReducers({
// 	onboarding: Onboarding,
// 	heroes: Heroes,
// });
const reducers = combineReducers({heroes: Heroes})

export default function configureStore(initialState, persistedReducer) {
	// const bundle = compose(applyMiddleware(thunkMiddleware));
	// const createStoreWithMiddleware = bundle(createStore);
	// const store = createStoreWithMiddleware(
	// 	reducers,
	// 	initialState,
	// 	persistedReducer,
	// 	window.devToolsExtension ? window.devToolsExtension() : f => f,
	// );
	const store = createStore(
		reducers,
		initialState,
		persistedReducer, 
		window.devToolsExtension ? window.devToolsExtension() : f => f,
	);

	return store;
}
