// Libs
import React, { Component } from 'react';
import styled from 'styled-components';

// Images
import bg from '../assets/onboardingp.jpeg';

// Components
import Input from '../components/Input';

// Services

// Styles

const Container = styled.div`
  display: flex;
  height: 100vh;
`;

const ContainerBg = styled.aside`
  background-image: url(${bg});
  background-position: bottom;
  width: 40%;
`;

const Content = styled.div`
  width: 60%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const ContainerAccount = styled.form`
  width: 65%;
`;

const Title = styled.h1`
  margin-bottom: 1.5rem;
  font-size: 2.5rem;
`;

const ButtonSubmit = styled.button`
  width: 80%;
  height: 3rem;
  border-radius: 10px;
  border: none;
  background: #2EC4B6;
`;

const AnotherOptionText = styled.p`
  justify-content: center;
  text-align: center;

  span {
    border-bottom: 2px solid #2EC4B6;
    cursor: pointer;
  }
`;

class Onboarding extends Component {
  state = {
    isCreateAccount: false,
  };

  handleCreateAccount = () => {
    console.log('create account');
  }

  render() {
    return (
      <Container>
        <Content>
          <ContainerAccount>
            <Title>Log in</Title>
              <Input
                type="email" 
                label="E-mail"
                placeholder="you@example.com"
              />
              <Input
                type="password" 
                label="Senha"
                placeholder="Enter you password"
              />
              <ButtonSubmit>Log in</ButtonSubmit>
            <AnotherOptionText>Você ainda não possui uma conta? <span onClick={this.handleCreateAccount}>Crie agora</span></AnotherOptionText>
          </ContainerAccount>
        </Content>
        <ContainerBg>
          {/* <p>background</p> */}
        </ContainerBg>
      </Container>
    )
  }
}

export default Onboarding;
