// Libs
import React, { useState } from 'react';
import styled from 'styled-components';

// Images
import CloseIcon from '../assets/x.svg';

//Styles
const Container = styled.div`
  margin-bottom: 2rem;  
  width: 20rem;
  height: 23rem;
  border: 1px solid #2EC4B6;
  border-radius: 8px;
  box-shadow: 5px 2px 10px 0px #2ec4b654;

  &:hover {
    border-color: #249b90;
    box-shadow: 5px 2px 10px 0px #249b90;
  }
`;

const HeroName = styled.p`
  margin: 1rem;
  color: #2EC4B6;
  text-align: center;
  font-size: 1rem;
  font-weight: 600;
  word-break: break-all;
`;

const HeroFigureImage = styled.figure`
  height: 70%;
  max-height: 70%;
  display: flex;
  justify-content: center;
`;

const HeroImage = styled.img`
  width: 65%;
`;

const Overlay = styled.div`
  position: fixed;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: rgba(196, 196, 196, 0.3);
  z-index: 2;
`;

const ContainerChoiceDetails = styled.div`
  margin-top: .5rem;
  width: 100%;
  display: flex;
  justify-content: center;
`;

const ChoiceDetailsItem = styled.span`
  margin: .2rem;
  padding: .5rem;
  color: #fff;
  background-color: #2EC4B6;
  border-radius: 15px;
  cursor: pointer;

  &:hover {
    background-color: #249b90;
  }
`;

const ContainerDetails = styled.div`
  position: relative;
  width: 30%;
  max-width: 30%;
  height: auto;
  background-color: #fff;
  border-radius: 10px;

  @media(max-width: 768px) {
    width: 60%;
    max-width: 60%;
  }

  @media(max-width: 648px) {
    width: 90%;
    max-width: 90%;
  }
`;

const DetailsTitle = styled.h2`
  margin: 1rem;
  text-align: center;
`;

