// Action Type
const ADD_HERO = 'superHeroes/ADD/ADD_HERO';
const SEARCH_HERO = 'superHeroes/SEARCH/SEARCH_HERO';
const FAV_HERO = 'superHeros/FAV/FAV_HERO';
// const DELETE_TASK = 'superHeros/DELETE/DELETE_TASK';

const initialState = {
  listSearch: [],
  listFavorites: [],
};

// Reducers
export default function Reducer(state = initialState, action) {
  switch(action.type) {
    case SEARCH_HERO:
      return Object.assign({}, state, {
        listSearch: [
          ...action.info
        ]
      });
    case FAV_HERO:
      return Object.assign({}, state, {
        listFavorites: [
          ...state.listFavorites, action.info
        ]
      });
    // case DELETE_TASK: 
    //   return Object.assign({}, state, {
    //     list: state.list.filter(item => item.id !== action.info.id)
    //   });
    default: return state;
  }
}

// Action Creator
export const addHero = info => ({
  type: ADD_HERO,
  info,
});

export const searchHero = info => ({
  type: SEARCH_HERO,
  info,
});

export const favHero = info => ({
  type: FAV_HERO,
  info,
});

